//    AlertTweets.php
//
//    arpsc_tweets displays the mi_arpsc twitter timeline inside
//    a normal mi-arpsc frame.  The timeline is generated by some
//    javascript provided by twitter.
//
    include('includes/session.inc');
    $title=_('Homeland Security Alerts');

    include('includes/functions.inc');

function tweets( $provider, $background, $color, $links, $height, $tback, $tfore, $providername )
{
  echo "     <p>&nbsp;</p><h1>" . $providername . "</h1>\n";
  echo "      <script src=\"http://widgets.twimg.com/j/2/widget.js\"></script>\n";
  echo "      <script>\n";
  echo "      new TWTR.Widget({\n";
  echo "	version: 2,\n";
  echo "	type: 'profile',\n";
  echo "	rpp: 8,\n";
  echo "	interval: 3000,\n";
  echo "        title: '" . $providername . "',\n";
  echo "        subject: '" . $provider . "',\n";
  echo "	width: 450,\n";
  echo "	height: $height,\n";
  echo "      theme: {\n";
  echo "	shell: {\n";
  echo "	  background: '" . $tback . "',\n";
  echo "	  color: '" . $tfore . "'\n";
  echo "	   },\n";
  echo "	   tweets: {\n";
  echo "	   background: '" . $background . "',\n";
  echo "	   color: '" . $color . "',\n";
  echo "	   links: '" . $links . "'\n";
  echo "	   }\n";
  echo "	 },\n";
  echo "       features: {\n";
  echo "	scrollbar: false,\n";
  echo "	loop: true,\n";
  echo "	live: true,\n";
  echo "	hashtags: true,\n";
  echo "	timestamp: true,\n";
  echo "	avatars: true,\n";
  echo "	behavior: 'all'\n";
  echo "       }\n";
  echo "    }).render().setUser('" . $provider . "').start();\n";
  echo "      </script>\n";
}

    // Remember the launch time
    $starttime = strftime("%A, %B %d %Y, %H:%M");

    // Open the database
    $db = mysql_connect($host , $dbuser, $dbpassword);
    mysql_select_db($DatabaseName,$db);

    ARESheader($title,"ARES RACES ARPSC Michigan Section Twitter");

    ARESleftBar( $db );
?>
  <div id="main">
<center>
    <h1>Homeland Security Alerts</h1>

<script src="http://widgets.twimg.com/j/2/widget.js"></script>
<script>
new TWTR.Widget({
