  type: 'list',
  rpp: 1,
  interval: 6000,
  title: 'NTASAlerts',
  subject: 'Recent DHS Alerts',
  width: 450,
  height: 100,
  theme: {
    shell: {
      background: '#000080',
      color: '#ffffff'
    },
    tweets: {
      background: '#600000',
      color: '#ffffff',
      links: '#ccccff'
    }
  },
  features: {
    scrollbar: true,
    loop: false,
    live: true,
    hashtags: true,
    timestamp: true,
    avatars: true,
    behavior: 'all'
  }
}).render().setList('NTASAlerts', '').start();
</script>
